mapper.select:
select * from cz_js_student_reserves


        and order_id = #{orderId,jdbcType=BIGINT}


        and stunum = #{stunum,jdbcType=CHAR}


        and inscode = #{inscode,jdbcType=CHAR}


        and school_name = #{schoolName,jdbcType=VARCHAR}


        and plat_user_id = #{platUserId,jdbcType=INTEGER}


        and cardtype = #{cardtype,jdbcType=TINYINT}


        and idcard = #{idcard,jdbcType=VARCHAR}


        and nationality = #{nationality,jdbcType=VARCHAR}


        and name = #{name,jdbcType=VARCHAR}


        and sex = #{sex,jdbcType=TINYINT}


        and phone = #{phone,jdbcType=VARCHAR}


        and address = #{address,jdbcType=VARCHAR}


        and photo = #{photo,jdbcType=VARCHAR}


        and fingerprint = #{fingerprint,jdbcType=VARCHAR}


        and id_card_img1 = #{idCardImg1,jdbcType=VARCHAR}


        and id_card_img2 = #{idCardImg2,jdbcType=VARCHAR}


        and report_img = #{reportImg,jdbcType=VARCHAR}


        and busitype = #{busitype,jdbcType=TINYINT}


        and drilicnum = #{drilicnum,jdbcType=VARCHAR}


        and fstdrilicdate = #{fstdrilicdate,jdbcType=DATE}


        and perdritype = #{perdritype,jdbcType=VARCHAR}


        and traintype = #{traintype,jdbcType=VARCHAR}


        and apply_fee = #{applyFee,jdbcType=DECIMAL}


        and bank_account = #{bankAccount,jdbcType=VARCHAR}


        and pay_token = #{payToken,jdbcType=VARCHAR}


        and info_check = #{infoCheck,jdbcType=INTEGER}


        and manager_check = #{managerCheck,jdbcType=INTEGER}


        and exam_check = #{examCheck,jdbcType=INTEGER}


        and shift_seq = #{shiftSeq,jdbcType=VARCHAR}


        and shift_info = #{shiftInfo,jdbcType=VARCHAR}


        and order_status = #{orderStatus,jdbcType=INTEGER}


        and created_at = #{createdAt,jdbcType=TIMESTAMP}


        and updated_at = #{updatedAt,jdbcType=TIMESTAMP}


        and deleted_at is null
