mapper.select:
select * from cz_order_refunds


        and stunum = #{stunum,jdbcType=CHAR}


        and inscode = #{inscode,jdbcType=CHAR}


        and student_name = #{studentName,jdbcType=CHAR}


        and school_name = #{schoolName,jdbcType=CHAR}


        and order_id = #{orderId,jdbcType=BIGINT}


        and refund_order_id = #{refundOrderId,jdbcType=BIGINT}


        and refund_fee = #{refundFee,jdbcType=DECIMAL}


        and refund_remark = #{refundRemark,jdbcType=VARCHAR}


        and merchant_id = #{merchantId,jdbcType=VARCHAR}


        type = #{type,jdbcType=INTEGER}


        obj_id = #{objId,jdbcType=INTEGER}


        and created_at = #{createdAt,jdbcType=TIMESTAMP}


        and updated_at = #{updatedAt,jdbcType=TIMESTAMP}


        and deleted_at is null
